mapper.select:
select user_id
  from ORD_D_DEAL
 where order_id = ${order_id}

mapper.update:
update ord_d_base
   set order_state = #{order_state} ,
       income_money = ${income_money}
 where order_id = ${order_id}

update ord_d_card_pay
   set order_state = #{order_state} ,
       income_money = ${income_money}
 where order_id = ${order_id}

mapper.select:
select order_id, pay_order, pay_type, pay_mode, pay_state, pay_fee, pay_time
  from ord_d_pay
 where order_id = ${order_id}

mapper.update:
update ord_d_pay
   set pay_state = #{pay_state},
       pay_time = sysdate
 where order_id = ${order_id}
   
   and pay_mode = #{pay_mode}

update ord_d_pay
   set pay_state = #{pay_state}
 where order_id = ${order_id}

update ord_d_pay
   set pay_state = 
       (
         
           when pay_mode=#{item.pay_mode} then #{item.pay_state}
         
       ),
       pay_time = sysdate
 where order_id = ${order_id}

mapper.select:
select phone_number
  from ord_d_cust
 where order_id = ${order_id}

select goods_name
  from ord_d_prod
 where order_id = ${order_id}

select order_id, pay_order, pay_type, pay_mode
  from ord_d_pay
 where order_id = ${order_id}

mapper.update:
update ord_d_refund
   set refund_state = #{refund_state}
 where order_id = ${order_id}

update ord_d_base
   set order_state = #{order_state}
 where order_id = ${order_id}

mapper.select:
select order_id, order_state
  from ord_d_refund
 where order_id = ${order_id}

select a.acct_id, a.fee, b.balance, b.version
  from act_d_accesslog a,
       act_d_account b
 where a.order_id = ${order_id}
   and a.trade_type = '22'
   and a.acct_id = b.acct_id

mapper.update:
update act_d_coupon set status = #{status}, update_time = sysdate
, order_id = ${order_id}, use_time = sysdate
where coupon_id = ${coupon_id}

mapper.select:
select user_id, acct_id, to_char(balance) balance, version
  from act_d_account
 where acct_id = #{acct_id}

mapper.update:
update act_d_account
   set balance = ${balance},
       version = #{version}
 where acct_id = #{acct_id}
 
   and version < #{version}
