mapper.update:
update INV_D_RECHARGE_CARD set card_status = 'L', order_id = ${order_id}, update_time = sysdate where card_status = '1' and card_no in (
select card_no from INV_D_RECHARGE_CARD where card_value= ${key} and card_status = '1'
and rownum <= ${item}
)

mapper.select:
	    select card_no, card_value, card_password
          from INV_D_RECHARGE_CARD
         where card_status = '0'
         
           and card_value = ${card_value}
                    
         
           and card_no = #{card_no}
                  
         
           and rownum <= 20

select to_char(log_id) log_id, agent_id card_no, serial_number from itf_d_recharge_log where interface_type = '010202' and check_status is null
and log_id = #{log_id}
and rownum < 20

	    select to_char(log_id) log_id, agent_id card_no, password, serial_number, serial_number_type
          from itf_d_recharge_log
         where log_id = #{log_id}

mapper.update:
update ITF_D_RECHARGE_LOG
   set success_flag = #{success_flag},
       result_code = #{result_code}
      
        , agent_balance = #{agent_balance}
      
      
        , unicon_serial_num = #{unicon_serial_num}
                  
      
        , check_status = #{check_status}
                    
 where log_id = ${log_id}

update ITF_D_RECHARGE_LOG
   set charge_result = #{charge_result},
       charge_value = #{charge_value}
 where log_id = ${log_id}

update ITF_D_RECHARGE_LOG
   set check_status = #{check_status}
 where log_id = ${log_id}

update inv_d_recharge_card
   set card_status = #{card_status}
 where card_no = #{card_no}

mapper.select:
	    select agent_id card_no
          from itf_d_recharge_log
         where log_id = #{log_id}

mapper.update:
update inv_d_recharge_card
   set result_code = #{result_code}
 where card_no = #{card_no}

mapper.select:
	    select to_char(log_id) log_id, result_code
          from itf_d_recharge_log
         where log_id = ${log_id}

	    select to_char(log_id) log_id, result_code
          from itf_d_recharge_log
         where log_id = ${log_id}
           and interface_type = #{interface_type}

	    select to_char(log_id) log_id, charge_result, to_char(charge_value) charge_value
          from itf_d_recharge_log
         where log_id = ${log_id}
