mapper.select:
	
       select store_name, phone_number, weixin_id, user_img
         from AUR_D_AUTHINFO
        where user_id = ${user_id}

select a.user_id user_id, a.goods_id goods_id, b.ctlg_code ctlg_code, b.goods_name goods_name, c.add_price/1000 goods_price, d.goods_amount goods_amount, f.photo_links photo_links from GDS_D_ABLE_RCD a, GDS_D_INFO b, GDS_D_PRICE c, GDS_D_AMOUNT d, GDS_D_PHOTO f where a.user_id = ${user_id} and a.goods_id = b.goods_id and b.goods_state = '1' and a.goods_id = c.goods_id and a.goods_id = d.goods_id and d.amount_type = '2' and b.album_id = f.album_id
and b.MERCHANT_ID = #{mchId}
and f.default_tag = '0' order by a.seq_num

select * from ( select a.goods_id goods_id, b.ctlg_code ctlg_code, b.goods_name goods_name, c.add_price/1000 goods_price, a.goods_amount goods_amount, f.photo_links photo_links from GDS_D_AMOUNT a, GDS_D_INFO b, GDS_D_PRICE c, GDS_D_PHOTO f where a.amount_type = '2' and a.goods_id = b.goods_id and b.goods_state = '1' and b.ctlg_code in (4, 9)
and b.MERCHANT_ID = #{mchId}
and a.goods_id = c.goods_id and b.album_id = f.album_id and f.default_tag = '0'
and b.goods_id not in
${goodsId}
order by a.goods_amount desc ) m
where rownum <= ${rest_num}

select * from ( select a.goods_id goods_id, b.ctlg_code ctlg_code, b.goods_name goods_name, c.add_price/1000 goods_price, a.goods_amount goods_amount, f.photo_links photo_links from GDS_D_AMOUNT a, GDS_D_INFO b, GDS_D_PRICE c, GDS_D_PHOTO f where a.amount_type = '2' and a.goods_id = b.goods_id and b.goods_state = '1' and b.ctlg_code in (5, 10)
and b.MERCHANT_ID = #{mchId}
and a.goods_id = c.goods_id and b.album_id = f.album_id and f.default_tag = '0'
and b.goods_id not in
${goodsId}
order by a.goods_amount desc ) m
where rownum <= ${rest_num}

select * from ( select a.goods_id goods_id, b.ctlg_code ctlg_code, b.goods_name goods_name, c.add_price/1000 goods_price, a.goods_amount goods_amount, f.photo_links photo_links from GDS_D_AMOUNT a, GDS_D_INFO b, GDS_D_PRICE c, GDS_D_PHOTO f where a.amount_type = '2' and a.goods_id = b.goods_id and b.goods_state = '1' and b.ctlg_code in (6, 11)
and b.MERCHANT_ID = #{mchId}
and a.goods_id = c.goods_id and b.album_id = f.album_id and f.default_tag = '0'
and b.goods_id not in
${goodsId}
order by a.goods_amount desc ) m
where rownum <= ${rest_num}

	  select *
	    from (
      select m.*, rownum row_num
        from (	
         select a.goods_id  goods_id, 
                a.ctlg_code ctlg_code, 
                a.goods_name goods_name, 
                c.add_price/1000 goods_price, 
                b.goods_amount goods_amount,  
                f.photo_links photo_links  
           from GDS_D_INFO a,
                GDS_D_AMOUNT b,
                GDS_D_PRICE c,
                GDS_D_PHOTO f
          where a.ctlg_code in 
                
                   ${ctlg_code}
                
            
               and lower(a.goods_name) like '%'||#{keyword}||'%'
            
            and a.goods_state = '1'
            and a.goods_id = b.goods_id
            
            and a.MERCHANT_ID = #{mchId}
            
            and b.amount_type = '2' 
            and a.goods_id = c.goods_id
            and a.album_id = f.album_id
            and f.default_tag = '0'  
          order by 
          
		    
			  ${sortCol}
			  
		    
			  b.goods_amount 
		    
	                
          
		    
			  ${sort}
			  
		    
			  desc
		    
	      
          ) m
        )
	
        where row_num > (${pageNum}-1)*${pageSize}
          and row_num <= ${pageNum}*${pageSize}

select a.goods_id goods_id, a.ctlg_code ctlg_code, a.goods_name goods_name, c.add_price/1000 goods_price, b.goods_amount goods_amount, f.photo_links photo_links from GDS_D_INFO a, GDS_D_AMOUNT b, GDS_D_PRICE c, GDS_D_PHOTO f where a.goods_id in
${goods_id}
and a.goods_state = '1' and a.goods_id = b.goods_id and b.amount_type = '2' and a.goods_id = c.goods_id and a.album_id = f.album_id and f.default_tag = '0' order by a.goods_name
